{
  "character": {
    "priority": 1,
    "name": {
      "english": "KMS Z52",
      "chinese": "柯妮",
      "japanese": "Z52"
    },
    "basic_settings": {
      "gender": "Female",
      "阵营": "铁血",
      "身份": "1944型驱逐舰"
